    public Biome GetBiomeByIndex(int whichBiome)
    {
        switch(whichBiome)
        {
            default:
                return Biome.FOREST;
            case 1:
                return Biome.BOSS;
            case 2:
                return Biome.DESERT;
            case 3:
                return Biome.FOREST;
            case 4:
                return Biome.RUINS;
            case 5:
                return Biome.COLOSEUM;
            case 6:
                return Biome.SNOW;
        }
    }
